scanThenGetStyledRangeArray (NSString * inSourceString,
                             const char * inSourceFileName,
                             TC_UniqueArray <C_styledRange> & ioStyledRangeArray,
                             const sint32 inAffectedRangeLocation,
                             const sint32 inAffectedRangeLength,
